import React from "react";

const Product = (props) => {
  const { name, count } = props.product;
  return (
    <div>
      <span>{name}</span>
      <span
        className={
          count === 0 ? "badge badge-warning m-2" : "badge badge-primary m-2"
        }
      >
        {count}
      </span>
      <button
        onClick={() => props.onIncrement(props.product)}
        className="btn btn-primary btn-sm m-2"
      >
        +
      </button>

      <button
        onClick={() => props.onDecrement(props.product)}
        className="btn btn-primary btn-sm m-2"
      >
        -
      </button>
      
      <button
        onClick={() => props.onDelete(props.product)}
        className="btn btn-danger btn-sm m-2"
      >
        <i className="fas fa-trash"></i>
      </button>
    </div>
  );
};

export default Product;
