import React, { useEffect, useState } from "react";
import './productList.css';
import Product from "./product";
import axios from "axios";



function ProductList() {

 const[pagenumber,setPagenumber]= useState (1)
 const[products,setProducts]=useState();
 const catogories = ['Amazon','Raills','Apple','Facebook']

  useEffect(()=>{
   const getProduct = async()=>{

    
     try {
      const response = await axios.get(`http://localhost:3004/products?_limit=4&_page=${pagenumber}`)
      const data = await  response.data
      setProducts(data)
      console.log(data)
       
     } catch (error) {
       console.log(error);
       
     }
   }
   getProduct()
  },[pagenumber])

  console.log(pagenumber)

  return (
    <div>
        <h3 className="products"> Products</h3>
        <h2>product name</h2>
        {/* <input type="text" name="Product name" placeholder="Product Name" className="col-lg-12"/> */}
        <button onClick={()=>{setPagenumber(pagenumber-1)}}disabled={pagenumber==1?true:false}>pre</button>
        <div className="products-container ">
          {products?products.map(item=>{
            return <Product
            image={item.image}
             address={item.address}
              product_name={item.product_name}
               brand_name={item.brand_name}
                price={item.price}
                 date={item.date}
                  discription={item.discription}
            />
          }):'no product'}
            
           

        </div>
        <button onClick={()=>{setPagenumber(pagenumber+1)}}disabled={pagenumber==Math.ceil(56/4)?true:false}>next</button>

        {/* <input type="text" name="Product name" placeholder="Product Name" className="col-lg-12 mt-4"/>
        <div className="products-container ">
            <Product></Product>
            <Product></Product>
            <Product></Product>
            <Product></Product>

        </div> */}
    </div>
  );
}

export default ProductList;
