// import ReactDOM  from 'react-dom';

// import ShoppingCart from './components/shoppingCart';
// import FirstTable from './components/FirstTable'

// import "../node_modules/bootstrap/dist/css/bootstrap.css";
// import "../node_modules/@fortawesome/fontawesome-free/css/all.css";

// ReactDOM.render(<ShoppingCart/> , document.querySelector('#root'));
// ReactDOM.render(<FirstTable/> , document.querySelector('#root'));

import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Table } from 'antd';

const columns = [
    {
        title: 'Id',
        dataIndex: 'id',
        key: 'id',
        width: 30,
        sorter: (a, b) => a.id - b.id,
      },
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
    width: 100,
    fixed: 'left',
    filters: [
      {
        text: 'Joe',
        value: 'Joe',
      },
      {
        text: 'John',
        value: 'John',
      },
    ],
    onFilter: (value, record) => record.name.indexOf(value) === 0,
  },
  {
    title: 'Feilds',
    children: [
      {
        title: 'Id',
        dataIndex: 'id',
        key: 'id',
        width: 30,
        sorter: (a, b) => a.id - b.id,
      },
      {
        title: 'Name',
        dataIndex: 'name',
        key: 'name',
        width: 100,
        fixed: 'left',
        filters: [
          {
            text: 'Joe',
            value: 'Joe',
          },
          {
            text: 'John',
            value: 'John',
          },
        ],
        onFilter: (value, record) => record.name.indexOf(value) === 0,
      },
      {
        title: 'Type',
        dataIndex: 'type',
        key: 'type',
        width: 50,
      },
      {
        title: 'Is Mandatory',
        dataIndex: 'is mandatory',
        key: 'is mandatory',
        width: 70,
      },
      {
        title: 'Options',
        children: [
          {
            title: 'Street',
            dataIndex: 'street',
            key: 'street',
            width: 150,
          },
          {
            title: 'Block',
            children: [
              {
                title: 'Building',
                dataIndex: 'building',
                key: 'building',
                width: 100,
              },
              {
                title: 'Door No.',
                dataIndex: 'number',
                key: 'number',
                width: 100,
              },
            ],
          },
        ],
      },
    ],
  },
 
];

const data = [];
for (let i = 0; i < 100; i++) {
  data.push({
    key: i,
    name: 'John Brown',
    id: i + 1,
    street: 'Lake Park',
    building: 'C',
    number: 2035,
    companyAddress: 'Lake Street 42',
    companyName: 'SoftLake Co',
    gender: 'M',
  });
}

ReactDOM.render(
  <Table
    columns={columns}
    dataSource={data}
    bordered
    size="middle"
    scroll={{ x: 'calc(700px + 50%)', y: 240 }}
  />,
  document.querySelector('#root'),
);
