import React, { Component } from "react";
import Product from "./product";

class ShoppingCart extends Component {
  state = {
    products: [
      { id: 1, name: "Burger", count: 0, price: 30 },
      { id: 2, name: "Fries", count: 0, price: 20 },
      { id: 3, name: "Cola", count: 0, price: 10 },
    ],
  };

  handleDelete = (product) => {
    // Clone
    const newProducts = [...this.state.products];
    // Edit
    const filteredProducts = newProducts.filter((p) => p.id !== product.id);
    // Set State
    this.setState({ products: filteredProducts });
  };

  handleReset = () => {
    // Clone
    let products = [...this.state.products];
    // Edit
    products = products.map((p) => {
      return { ...p, count: 0 };
    });
    // Set State
    this.setState({ products });
  };

  //   ?????
  handleIncrement = (product) => {
    // Clone
    let products = [...this.state.products];
    const index = products.indexOf(product);
    products[index] = { ...products[index] };
    // Edit
    products[index].count++;
    // Set State
    this.setState({ products });
  };

  ///decrement
  handleDecrement = (product) => {
    // Clone
    let products = [...this.state.products];
    const index = products.indexOf(product);
    products[index] = { ...products[index] };
    // Edit
    products[index].count--;
    // Set State
    this.setState({ products });
  };

  render() {
    return (
      <div>
        <h3>Shopping Cart</h3>
        {this.state.products.map((prdct) => (
          <Product
            key={prdct.id}
            product={prdct}
            onDelete={this.handleDelete}
            onIncrement={this.handleIncrement}
            onDecrement={this.handleDEcrement}
          />
        ))}
        <button onClick={this.handleReset} className="btn btn-secondary m-2">
          Reset
        </button>
      </div>
    );
  }
}

export default ShoppingCart;
