// import ReactDOM  from 'react-dom';

// import ShoppingCart from './components/shoppingCart';

// import "../node_modules/bootstrap/dist/css/bootstrap.css";
// import "../node_modules/@fortawesome/fontawesome-free/css/all.css";

// ReactDOM.render(<ShoppingCart/> , document.querySelector('#root'));

import React,{ useState, useEffect} from 'react';
import ReactDOM from 'react-dom';
import { useForm } from "react-hook-form";


import 'antd/dist/antd.css';
import { Table } from 'antd';
import TabbleForm from './components/TableForm'

const[statedata,Setstatedata]= useState ({
  data:{ 
    "id": 1,
    "name": "نموذج 1",
     feilds:[]}
})
console.log(statedata)



useEffect(()=>{
  getFeilds()
},[]
  )


const columns = [
    {
        title: 'Id',
        dataIndex: 'id',
        key: 'id',
        width: 30,
        sorter: (a, b) => a.id - b.id,
      },
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
    width: 50,
    
    filters: [
      {
        text: 'Joe',
        value: 'Joe',
      },
      {
        text: 'John',
        value: 'John',
      },
    ],
    onFilter: (value, record) => record.name.indexOf(value) === 0,
  },
  {
    title: 'Feilds',
    children: [
      {
        title: 'Id',
        dataIndex: 'id',
        key: 'id',
        width: 30,
        sorter: (a, b) => a.id - b.id,
      },
      {
        title: 'Name',
        dataIndex: 'name',
        key: 'name',
        width: 50,
        
        filters: [
          {
            text: 'Joe',
            value: 'Joe',
          },
          {
            text: 'John',
            value: 'John',
          },
        ],
        onFilter: (value, record) => record.name.indexOf(value) === 0,
      },
      {
        title: 'Type',
        dataIndex: 'type',
        key: 'type',
        width: 30,
      },
      {
        title: 'Is Mandatory',
        dataIndex: 'is mandatory',
        key: 'is mandatory',
        width: 30,
      },
      {
        title: 'Options',
        children: [
         
            {
                title: 'Id',
                dataIndex: 'id',
                key: 'id',
                width: 30,
                sorter: (a, b) => a.id - b.id,
              },
              {
                title: 'Name',
                dataIndex: 'name',
                key: 'name',
                width: 50,
               
                filters: [
                  {
                    text: 'Joe',
                    value: 'Joe',
                  },
                  {
                    text: 'John',
                    value: 'John',
                  },
                ],
                onFilter: (value, record) => record.name.indexOf(value) === 0,
              },
              {
                title: 'Has Comment',
                dataIndex: 'has comment',
                key: 'has comment',
                width: 50,
                
              },
        ],
      },
    ],
  },
 
];

const data = [];
for (let i = 0; i < 100; i++) {
  data.push({
    key: i,
    name: 'John Brown',
    id: i + 1,
   
  });
}
const getFeilds = async () => {
  try {
    const response = await axios.get(`http://localhost:3000/feilds`);
    const data = await response.data;
    console.log(data);
    Setstatedata(prevState=>{[...prevState,{feilds:data}]})
    console.log(statedata)
      
  } catch (error) {
      console.log(error)
      
  }

};

ReactDOM.render(
  <div>
    <TabbleForm/>
    <Table
    columns={columns}
    dataSource={data}
    bordered
    size="middle"
    scroll={{ x: 'calc(700px + 50%)', y: 240 }}
    // style={{width:"70%"}}
  />
  
  </div>,
  document.querySelector('#root'),
);
